            public void actionPerformed(java.awt.event.ActionEvent evt)
		{
			String f_path = new String(jTextField1.getText());
			try{
	                jButton3ActionPerformed(evt);
			if(f_path.equals(""))
			{
				
				JLabel errorFields = new JLabel("<HTML><FONT COLOR = Blue>File Path Required.</FONT></HTML>");	
				JOptionPane.showMessageDialog(null,errorFields); 
				
			}
			else
				{
				
				BufferedWriter fileOut = new BufferedWriter(new FileWriter("/etc/Network_Builder_V0.1/logs/FTP/ftpk.log"));
				fileOut.write(jTextField1.getText()); 
				fileOut.newLine();
				fileOut.close();

				jTextField1.setText("");
				String unixCommand = "bash ftp.sh "+f_path; 
				runShellScript(unixCommand);

				String str1;
				BufferedReader br1=new BufferedReader(new FileReader("/etc/Network_Builder_V0.1/logs/NAT/natk.msg"));
				while ((str1 = br1.readLine()) != null) 
				{
					jTextArea1.append(str1 + "\n");
				
				}
				
				
				
				
				}
				
			}
			catch(Exception e)		
			{
			}	
			
            }
